mapper.insert:
insert into t_subject (object_id, create_user, create_time, 
  update_user, update_time, deleted, 
  name, type_id, subject_answer,grade_count,every_grade_score,must_answer,name_image
  )
values (#{objectId,jdbcType=BIGINT}, #{createUser,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP},
  #{updateUser,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}, #{deleted,jdbcType=TINYINT},
  #{name,jdbcType=VARCHAR}, #{typeId,jdbcType=INTEGER}, #{subjectAnswer,jdbcType=BIGINT},#{gradeCount,jdbcType=INTEGER},#{everyGradeScore,jdbcType=VARCHAR},
  #{mustAnswer,jdbcType=TINYINT},#{nameImage,jdbcType=VARCHAR}
  )

mapper.select:
SELECT
FROM t_subject
object_id in( SELECT subject_id FROM t_questionnaire_subject qs
and questionnaire_id = #{questionnaireId}
)
and name LIKE '${name}%'
